from odoo import api, fields, modelsclass AccountAccount(models.Model):    _inherit = 'account.account'    limit_balance = fields.Float(string="Limit Balance", required=False)    total_balance = fields.Float(string="Total Balance", required=False,compute="_compute_total_balance")    @api.depends()    def _compute_total_balance(self):        for x in self:            accountmoveline = self.env['account.move.line'].search([                    ('account_id', '=', x.id),                ('move_id.state','=','posted')            ])            debit=sum(accountmoveline.mapped('debit'))            credit=sum(accountmoveline.mapped('credit'))            x.total_balance=debit-credit